mapper.select:
SELECT COUNT(*)
FROM REVIEWS_LIKES
WHERE review_num = #{reviewNum} AND user_nick = #{userNick}

SELECT COUNT(*)
FROM REVIEWS_LIKES
WHERE review_num = #{reviewNum}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM REVIEWS_LIKES
WHERE review_num = #{reviewNum} AND user_nick = #{userNick}
